import React, { Component } from 'react';
import axios from 'axios';

export default class Admin extends Component{
    constructor(props){
        super(props);
        this.state = {
            pdData: [],
            form: {
                pdId: {
                    value: ''
                },
                pdName: {
                    value: ''
                },
                pdUrl: {
                    value: ''
                },
                qty: {
                    value: 0
                },
                price: {
                    value: 0
                },
                desc: {
                    value: ''
                }
            },
            intervalIsSet: false
        }
        this.getProductData = this.getProductData.bind(this);
        this.addProduct = this.addProduct.bind(this);
        this.updateProduct = this.updateProduct.bind(this);
        this.delProduct = this.delProduct.bind(this);
        this.handleChange = this.handleChange.bind(this);
    }

    // fetch all existing data 
    componentDidMount() {
        this.getProductData();
        if(!this.state.intervalIsSet) {
            let interval = setInterval(this.getProductData, 1000);
            this.setState({
                intervalIsSet: interval
            });
        }
    }

    // always kill a process everytime we are done using it 
    componentWillUnmount() {
        if(this.state.intervalIsSet){
            clearInterval(this.state.intervalIsSet);
            this.setState({
                intervalIsSet: null
            });
        }
    }

    // get all Data from database 
    getProductData = () => {
        fetch('http://localhost:8080/api/products')
        .then(res => res.json())
        .then(data => {
            this.setState({
                pdData: data
            })
        });
    };

    handleChange = (e) => {
        const name = e.target.name;
        const value = e.target.value;

        this.setState({
            form: {
                [name]: value
            }
        });
    } 

    // add Product data to databasse
    addProduct = () => {
        console.log(this.state.pdId.value);

      axios.post('http://localhost:8080/api/products', {
          id: this.state.form.pdId.value,
          name: this.state.form.pdName.value,
          picURL: this.state.form.pdUrl.value,
          quantity: this.state.form.qty.value,
          price: this.state.form.price.value,
          description: this.state.form.desc.value
      }).then((res) => {
          console.log(res);
      }).catch((err) => {
          console.log(err);
      })
    }
    // update Product data to database
    updateProduct = () => {

    }
    // delete Product data from database
    delProduct = () => {

    }

    render() {
        return (
            <div>
                {/* input name and state key name should be same  */}
                <form onSubmit={this.addProduct}>
                    <div className="form-group">
                        <label htmlFor="product-id">Product Id</label>
                        <input type="text" className="form-control" aria-describedby="id-help" name="pdId" value={this.state.form.pdId.value} onChange={this.handleChange} />
                        <small id="id-help" className="form-text text-muted">Must be integer</small>
                    </div>
                    <div className="form-group">
                        <label htmlFor="product-name">Product Name</label> 
                        <input type="text" className="form-control" name="pdName" value={this.state.form.pdName.value} onChange={this.handleChange} />
                    </div>
                    <div className="form-group">
                        <label htmlFor="product-pic">Photo URL</label>
                        <input type="text" className="form-control" name="pdUrl" value={this.state.form.pdUrl.value} onChange={this.handleChange}/>
                    </div>
                    <div className="form-group">
                        <label htmlFor="product-qty">Qty</label> 
                        <input type="number" className="form-control" name="qty" value={this.state.form.qty.value} onChange={this.handleChange} />
                    </div>
                    <div className="form-group">
                        <label htmlFor="product-price">Price</label> 
                        <input type="number" className="form-control" name="price" value={this.state.form.price.value} onChange={this.handleChange} />
                    </div>
                   <div className="form-group">
                        <label htmlFor="product-desc">Description</label>          
                        <input type="text" className="form-control" name="desc" value={this.state.form.desc.value} onChange={this.handleChange}/>
                   </div>
                    <button className="btn btn-primary" type="submit">Add</button>
                </form>
            </div>
        )
    }
}
