import React, { Component } from 'react';
import Display from './Display';
import Container from './Container';

export default class Products extends Component{
    constructor(props){
        super(props);
        this.state = {
            pdList: [],
            value: '',
            intervalIsSet: false
        }
        this.handleChange = this.handleChange.bind(this);
    }

    // fetch all existing data 
    componentDidMount() {
        this.getProductData();
        if(!this.state.intervalIsSet) {
            let interval = setInterval(this.getProductData, 1000);
            this.setState({
                intervalIsSet: interval
            });
        }
    }

    // always kill a process everytime we are done using it 
    componentWillUnmount() {
        if(this.state.intervalIsSet){
            clearInterval(this.state.intervalIsSet);
            this.setState({
                intervalIsSet: null
            });
        }
    }

    // get Data from database 
    getProductData = () => {
        fetch('http://localhost:8080/api/products')
        .then(res => res.json())
        .then(data => {
            this.setState({
                pdList: data
            })
        });
    };

    // handle sorting the product list 
    handleChange = (e) => {
        this.setState({
            value: e.target.value
        });

        let sortedData = [];

        if(this.state.value === 'name'){
            sortedData = this.state.pdList.sort((a, b) => {
                // ingnore case
                var nameA = a.name.toUpperCase();
                var nameB = b.name.toUpperCase();
                if(nameA < nameB) return -1;
                if(nameA > nameB) return 1;

                return 0;
            })    
        }
    }

    render() {
        let pd = this.state.pdList.map((el) => {
            return(
                <Display id={el.id} name={el.name} price={el.price} pic={el.picURL}/>
            )
        })
        return (
            <Container title={this.props.title}>
                <div className="search-bar">
                    <span>Order By</span>
                    <select value={this.state.value} onChange={this.handleChange}>
                        <option value="select">Select</option>
                        <option value="name">Name</option>
                        <option value="price">Price</option>
                    </select>
                </div>
                <div className="flex-container">
                    {pd}
                </div>
            </Container>
        )
    }
}
