    // add Product data to databasse
    addProduct = () => {

    }
    // update Product data to database
    updateProduct = () => {

    }
    // delete Product data from database
    delProduct = () => {

    }

    render() {
        return (
            <div>
                {/* input name and state key name should be same  */}
                <form onSubmit={this.addProduct}>
                    <div className="form-group">
                        <label>Product Id</label>
                        <input type="text" className="form-control" aria-describedby="id-help" name="pdId" value={this.state.form.pdId.value} onChange={this.handleChange}/>
                        <small id="id-help" className="form-text text-muted">Must be integer</small>
                    </div>
                    <div className="form-group">
                        <label>Product Name</label> 
                    <input type="text" name="pdName" value={this.state.form.pdName.value} onChange={this.handleChange}/>
                    </div>

                    photo URL:
                    <input type="text" name="pdUrl" value={this.state.form.pdUrl.value} onChange={this.handleChange}/>
                    Qty: 
                    <input type="number" name="qty" value={this.state.form.qty.value} onChange={this.handleChange}/>
                    Price: 
                    <input type="number" name="price" value={this.state.form.price.value} onChange={this.handleChange}/>
                    Description: 
                    <input type="text" name="desc" />
                    <button type="submit">Add</button>
                </form>
            </div>
        )
    }
}
